mapper.select:
select e.*, t.* from export_c e left join ( select ep.export_id,ep.export_product_id,ep.product_no as ep_product_no,ep.product_desc as ep_product_desc,ep.cnumber as ep_cnumber, ep.packing_unit as ep_packing_unit,ep.box_num as ep_box_num,ep.price as ep_price, ep.amount as ep_amount,ep.gross_weight as ep_gross_weight,ep.net_weight as ep_net_weight, ep.factory_id,ep.factory_name,ep.full_name,ep.contactor,
ext.ext_eproduct_id,ext.product_no as ext_product_no,ext.product_desc as ext_product_desc,ext.cnumber as ext_cnumber, ext.packing_unit as ext_packing_unit,ext.box_num as ext_box_num,ext.price as ext_price, ext.amount as ext_amount,ext.gross_weight as ext_gross_weight,ext.net_weight ext_net_weight, ext.factory_id as ext_factory_id,ext.factory_name as ext_factory_name,ext.full_name as ext_full_name,ext.contactor as etx_contactor
from ( select ep.export_id,ep.export_product_id,ep.product_no,ep.product_desc,ep.cnumber,ep.packing_unit,ep.box_num,ep.price, ep.amount,ep.gross_weight,ep.net_weight, f.factory_id,f.factory_name,f.full_name,f.contactor from export_product_c ep left join factory_c f on f.factory_id=ep.factory_id ) ep left join ( select ext.ext_eproduct_id,ext.export_product_id,ext.product_no,ext.product_desc,ext.cnumber,ext.packing_unit,ext.box_num,ext.price, ext.amount,ext.gross_weight,ext.net_weight, f.factory_id,f.factory_name,f.full_name,f.contactor from ext_eproduct_c ext left join factory_c f on f.factory_id=ext.factory_id ) ext on ep.export_product_id=ext.export_product_id ) t on t.export_id=e.export_id where e.export_id=#{id}

mapper.update:
update export_c
INPUT_DATE=#{inputDate}, LCNO=#{lcno}, CONSIGNEE=#{consignee}, TRANSPORT_MODE=#{transportMode}, SHIPMENT_PORT=#{shipmentPort}, PRICE_CONDITION=#{priceCondition}, MARKS=#{marks}, REMARK=#{remark},
where export_id=#{id}

mapper.delete:
delete from export_c
where export_id in 

	#{id}
